INSERT INTO `computer`.`news` (`id`,`title`,`describe`,`content`,`cover`,`slideshow_cover`,`type`,`contributor`,`is_hot`,`is_top`,`is_status`,`count`,`column_id`,`user_id`,`creation_time`,`modify_time`) VALUES (44, '计算机工程系简介', '测试', '<p style=\"box-sizing: border-box;padding: 0px;font-family: &#39;Helvetica Neue&#39;, Helvetica, Roboto, Arial, sans-serif;font-size: 1rem;line-height: 1.6;text-rendering: optimizelegibility;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255);text-indent: 40px\"><span style=\"box-sizing: border-box;font-family: 宋体;font-size: 20px\">计算机工程系承担着计算机专业人才的培养，及全院计算机相关的基础课程，为社会提供技术支持和服务。坚持以学生为中心，以“价值观<span style=\"box-sizing: border-box\">+</span>知识<span style=\"box-sizing: border-box\">+</span>技能”为培养目标，形成了岗位驱动、项目导向等教学模式，通过专业技能大赛、职业素质培训等活动，不断提升学生的综合素质，为融入社会打下良好的基础。</span></p><p style=\"box-sizing: border-box;padding: 0px;font-family: &#39;Helvetica Neue&#39;, Helvetica, Roboto, Arial, sans-serif;font-size: 1rem;line-height: 1.6;text-rendering: optimizelegibility;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255);text-indent: 40px\"><span style=\"box-sizing: border-box;line-height: inherit;color: rgb(227, 61, 102)\"><span style=\"box-sizing: border-box;color: red;font-family: 宋体;font-size: 20px\">专业设置：</span></span><span style=\"box-sizing: border-box;font-family: 宋体;font-size: 20px\">开设计算机应用技术专业（平面设计）、通信技术专业、数字媒体应用技术专业、软件技术专业<span style=\"box-sizing: border-box\">、计算机网络技术专业。</span></span></p><p style=\"box-sizing: border-box;padding: 0px;font-family: &#39;Helvetica Neue&#39;, Helvetica, Roboto, Arial, sans-serif;font-size: 1rem;line-height: 1.6;text-rendering: optimizelegibility;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255);text-indent: 40px\"><span style=\"box-sizing: border-box;font-family: 宋体;font-size: 20px\">其中，计算机应用技术和通信技术专业均为学院的重点建设专业。计算机应用技术专业获得广东省<span style=\"box-sizing: border-box\">2014</span>年实训基地建设项目。</span></p><p style=\"box-sizing: border-box;padding: 0px;font-family: &#39;Helvetica Neue&#39;, Helvetica, Roboto, Arial, sans-serif;font-size: 1rem;line-height: 1.6;text-rendering: optimizelegibility;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255);text-indent: 40px\"><span style=\"box-sizing: border-box;line-height: inherit;color: rgb(227, 61, 102)\"><span style=\"box-sizing: border-box;color: red;font-family: 宋体;font-size: 20px\">师资力量：</span></span><span style=\"box-sizing: border-box;font-family: 宋体;font-size: 20px\">拥有一支结构合理、专业素质较高、爱岗敬业、专兼结合的优秀教师队伍。全系现有教师<span style=\"box-sizing: border-box\">30</span>人，其中高、中级职称<span style=\"box-sizing: border-box\">27</span>人，硕士学位以上<span style=\"box-sizing: border-box\">24</span>人，“双师<span style=\"box-sizing: border-box\">”</span>素质教师占<span style=\"box-sizing: border-box\">70%</span>，&nbsp;<span style=\"box-sizing: border-box\">6</span>名教师获得省级技能大赛优秀指导教师称号，此外，还长期聘请行业、企业的专业技术人才担任兼职教师，充分发挥兼职教师的实践操作技能和企业运作的优势，使学生的职业技能得到锻炼和提高。</span></p><p style=\"box-sizing: border-box;padding: 0px;font-family: &#39;Helvetica Neue&#39;, Helvetica, Roboto, Arial, sans-serif;font-size: 1rem;line-height: 1.6;text-rendering: optimizelegibility;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255);text-indent: 40px\"><span style=\"box-sizing: border-box;line-height: inherit;color: rgb(227, 61, 102)\"><span style=\"box-sizing: border-box;color: red;font-family: 宋体;font-size: 20px\">实习实训条件：</span></span><span style=\"box-sizing: border-box;font-family: 宋体;font-size: 20px\">拥有移动应用开发实训室、软件技术实训室、综合布线实训室、集控仿真网络实训室、数字媒体实训室、平面设计实训室、多媒体制作实训室、<span style=\"box-sizing: border-box\">4G</span>全网在线仿真实训室、程序设计基础实训室等<span style=\"box-sizing: border-box\">10</span>间校内专业实训室。其中，移动应用开发实训室（苹果平台）是粤西高校第一个由苹果公司开发的基于<span style=\"box-sizing: border-box\">iOS</span>移动操作系统的实训室。与校外企事业单位深圳地铁集团、宝钢湛江钢铁有限公司、广州中星网络技术有限公司、南方通信建设有限公司、深圳凯立德股份有限公司、珠海爱浦京软件技术有限公司等多家单位建立校外实训基地。其中，广州中星网络技术有限公司实训基地被列为广东省高等学校质量工程（大学生校外实践教育基地）建设项目。</span></p><p style=\"box-sizing: border-box;padding: 0px;font-family: &#39;Helvetica Neue&#39;, Helvetica, Roboto, Arial, sans-serif;font-size: 1rem;line-height: 1.6;text-rendering: optimizelegibility;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255);text-indent: 40px\"><span style=\"box-sizing: border-box;line-height: inherit;color: rgb(227, 61, 102)\"><span style=\"box-sizing: border-box;color: red;font-family: 宋体;font-size: 20px\">教科研情况：</span></span><span style=\"box-sizing: border-box;font-family: 宋体;font-size: 20px\">近年，获省实训基地建设项目<span style=\"box-sizing: border-box\">1</span>项、省质量工程项目<span style=\"box-sizing: border-box\">1</span>项、省教改革项目<span style=\"box-sizing: border-box\">10</span>项、市级科研项目<span style=\"box-sizing: border-box\">5</span>项，实用型专利技术<span style=\"box-sizing: border-box\">3</span>项，公开发表论文近<span style=\"box-sizing: border-box\">50</span>篇，主编教材<span style=\"box-sizing: border-box\">6</span>本，<span style=\"box-sizing: border-box\">6</span>门院级精品课程已通过验收，<span style=\"box-sizing: border-box\">3</span>门院级精品开放课程。</span></p><p style=\"box-sizing: border-box;padding: 0px;font-family: &#39;Helvetica Neue&#39;, Helvetica, Roboto, Arial, sans-serif;font-size: 1rem;line-height: 1.6;text-rendering: optimizelegibility;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255);text-indent: 40px\"><span style=\"box-sizing: border-box;line-height: inherit;color: rgb(227, 61, 102)\"><span style=\"box-sizing: border-box;color: red;font-family: 宋体;font-size: 20px\">校企合作：</span></span><span style=\"box-sizing: border-box;font-family: 宋体;font-size: 20px\">与公诚管理咨询有限公司第三分公司、深圳凯立德科技股份有限公司、珠海爱浦京软件技术有限公司、广州国为信息科技有限公司、南方通信建设有限公司等多家大型企业建立了长期稳定的合作关系，并设立了订单班。与广州国为信息科技有限公司共建“双创”服务中心，将职业教育引入课堂，有效地提高了学生的就业竞争力。</span></p><p style=\"box-sizing: border-box;padding: 0px;font-family: &#39;Helvetica Neue&#39;, Helvetica, Roboto, Arial, sans-serif;font-size: 1rem;line-height: 1.6;text-rendering: optimizelegibility;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255);text-indent: 40px\"><span style=\"box-sizing: border-box;line-height: inherit;color: rgb(227, 61, 102)\"><span style=\"box-sizing: border-box;color: red;font-family: 宋体;font-size: 20px\">就业情况：</span></span><span style=\"box-sizing: border-box;font-family: 宋体;font-size: 20px\">各专业紧密结合就业岗位需求，毕业生深受用人单位欢迎。近几年毕业生就业率均超<span style=\"box-sizing: border-box\">99%</span>，均在省平均水平之上，专业对口率超过<span style=\"box-sizing: border-box\">85%</span>，所培养的学生适岗能力强，很多毕业生已成为企事业单位骨干，深受用人单位好评。</span></p><p style=\"box-sizing: border-box;padding: 0px;font-family: &#39;Helvetica Neue&#39;, Helvetica, Roboto, Arial, sans-serif;font-size: 1rem;line-height: 1.6;text-rendering: optimizelegibility;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255);text-indent: 40px\"><span style=\"box-sizing: border-box;line-height: inherit;color: rgb(227, 61, 102)\"><span style=\"box-sizing: border-box;color: red;font-family: 宋体;font-size: 20px\">技能大赛：</span></span><span style=\"box-sizing: border-box;font-family: 宋体;font-size: 20px\">坚持“以赛促学、以赛促教”，每年组织校级计算机技能竞赛，为学生提供了竞赛的平台，并选拔人才参加省级以上的各类技能大赛，取得了可喜的成绩。<span style=\"box-sizing: border-box\">2012</span>年以来，计算机工程系组织了<span style=\"box-sizing: border-box\">112</span>人次学生参加<span style=\"box-sizing: border-box\">26</span>项省级以上技能大赛，分别取得一、二、三等奖奖项<span style=\"box-sizing: border-box\">50</span>多项。</span></p><p style=\"box-sizing: border-box;padding: 0px;font-family: &#39;Helvetica Neue&#39;, Helvetica, Roboto, Arial, sans-serif;font-size: 1rem;line-height: 1.6;text-rendering: optimizelegibility;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255);text-indent: 40px\"><span style=\"box-sizing: border-box;line-height: inherit;color: rgb(227, 61, 102)\"><span style=\"box-sizing: border-box;color: red;font-family: 宋体;font-size: 20px\">素质拓展：</span></span><span style=\"box-sizing: border-box;font-family: 宋体;font-size: 20px\">坚持<span style=\"box-sizing: border-box\">“</span>以生为本<span style=\"box-sizing: border-box\">”</span>，以培养学生的创新精神和实践能力为重点，学生可通过青年志愿者协会等各种社团组织开展形式多样的活动，也可以加入教师工作室开展项目实战，培养学生的创新创业精神、职业素养和实践能力等，提高就业竞争力，毕业生普遍受到用人单位好评。此外，学生还可以进行学历提升，学院与华南理工大学的数字媒体技术专业、华南师范大学的人力资源管理专业等合作开办自考本科班，学生在取得本院大专毕业证书同时还可以获得由广东省自考委与华南理工大学、华南师范大学等合署颁发，教育部电子注册，属于国民教育系列的本科学历毕业证书及学士学位证书，为有能力、有志于继续提升学历的在校学生提供了有利的个人成长机会。</span></p><p style=\"box-sizing: border-box;padding: 0px;font-family: &#39;Helvetica Neue&#39;, Helvetica, Roboto, Arial, sans-serif;font-size: 1rem;line-height: 1.6;text-rendering: optimizelegibility;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255);text-indent: 40px\">&nbsp;</p><p style=\"box-sizing: border-box;padding: 0px;font-family: &#39;Helvetica Neue&#39;, Helvetica, Roboto, Arial, sans-serif;font-size: 1rem;line-height: 1.6;text-rendering: optimizelegibility;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255);text-indent: 40px\">&nbsp;</p><p><span style=\"box-sizing: border-box;color: rgb(51, 51, 51);text-indent: 28px;background-color: rgb(255, 255, 255);font-family: 宋体;font-size: 20px\"><p style=\"box-sizing: border-box;padding: 0px;font-family: inherit;font-size: 1rem;line-height: 1.6;text-rendering: optimizelegibility\"><span style=\"box-sizing: border-box;color: black;font-size: 19px\">地址： 广东省茂名市电白区沙院镇海城路五路<span style=\"box-sizing: border-box\">1</span>号<span style=\"box-sizing: border-box\">&nbsp;(</span>邮编：<span style=\"box-sizing: border-box\">525027)</span></span></p><p style=\"box-sizing: border-box;padding: 0px;font-family: inherit;font-size: 1rem;line-height: 1.6;text-rendering: optimizelegibility\"><span style=\"box-sizing: border-box;color: black;font-size: 19px\">系部网址：<span style=\"box-sizing: border-box\"><a href=\"http://www.mmvtc.cn/templet/jsjgcx/index.jsp\" style=\"box-sizing: border-box;color: rgb(0, 140, 186);line-height: inherit\"><span style=\"color:#0000ff\">http://www.mmvtc.cn/templet/jsjgcx/index.jsp</span></a></span></span></p><p style=\"box-sizing: border-box;padding: 0px;font-family: inherit;font-size: 1rem;line-height: 1.6;text-rendering: optimizelegibility\"><span style=\"box-sizing: border-box;color: black;font-size: 19px\">E-mail</span><span style=\"box-sizing: border-box;color: black;font-size: 19px\">：<span style=\"box-sizing: border-box\"><a href=\"mailto:mzjsj@126.com\" style=\"box-sizing: border-box;color: rgb(0, 140, 186);line-height: inherit\"><span style=\"color:#0000ff\">mzjsj@126.com</span></a>&nbsp;<span style=\"box-sizing: border-box\">&nbsp;&nbsp;</span></span>联系电话：<span style=\"box-sizing: border-box\">0668-2506786/2506766</span></span></p><p style=\"box-sizing: border-box;padding: 0px;font-family: inherit;font-size: 1rem;line-height: 1.6;text-rendering: optimizelegibility;text-indent: 40px\">&nbsp;</p><p style=\"box-sizing: border-box;padding: 0px;font-family: inherit;font-size: 1rem;line-height: 1.6;text-rendering: optimizelegibility;text-indent: 40px\"><span style=\"box-sizing: border-box;font-size: 20px\">(<span style=\"box-sizing: border-box;font-family: 仿宋_GB2312;font-size: 21px\"><span style=\"color:#000000\">以上数据统计截止<span style=\"box-sizing: border-box\">2017</span>年<span style=\"box-sizing: border-box\">9</span>月</span></span>)</span></p></span></p><p><br/></p>', '', '', '测试', '管理员', 0, 0, 1, 1, 29, 4, 1557998286, 1558061539),(45, '计算机工程系组织结构图', '测试', '<p><img src=\"/ueditor/php/upload/image/20190516/1557998351.jpg\"/></p>', '', '', '测试', '管理员', 0, 0, 1, 200, 29, 4, 1557998369, 1557998369),(46, '茂名职业技术学院计算机工程系各岗位工作职责', '测试', '<p><span style=\"box-sizing: border-box; line-height: inherit; color: rgb(227, 61, 102);\">一、系主任岗位职责</span></p><p>1. &nbsp;&nbsp; 认真贯彻执行党和国家的教育方针，落实学院的发展规划。按照学院发展的总体规划，制定本系的专业建设规划，年度预算。</p><p>2.&nbsp;&nbsp;&nbsp; 负责本系各专业的设置和新专业的申报，制定人才培养方案，落实本系专业教学计划、教学大纲。</p><p>3.&nbsp;&nbsp;&nbsp; 负责组织教学管理、教科研、专业建设、师资队伍建设、实验室建设等。</p><p>4.&nbsp;&nbsp;&nbsp; 组织开展教学教研的研究活动，组织各种教学经验交流和学术交流活动，促进教学质量的不断提高。</p><p>5.&nbsp;&nbsp;&nbsp; 组织制定各项管理制度，</p><p>6.&nbsp;&nbsp;&nbsp; 组织本系学生的课堂学习、实验、实习、毕业设计、毕业论文答辩等工作。</p><p>7.&nbsp;&nbsp;&nbsp; 毕业设计(论文)准备工作，对即将进入校外生产实习及毕业设计(论文)阶段的学生进行动员。</p><p>8.&nbsp;&nbsp;&nbsp; 组织本系教师承担教学任务，监督检查教师教学工作任务的完成情况。</p><p>9.&nbsp;&nbsp;&nbsp; 负责教师调课的批准、教学计划的修订、指导教研室安排教学任务。</p><p>10.&nbsp;&nbsp;负责精品专业和精品课程的建设任务。</p><p>11.&nbsp; 完成院领导交办的其它工作。</p><p>&nbsp;</p><p><span style=\"box-sizing: border-box; line-height: inherit; color: rgb(227, 61, 102);\">二、系总支书记职责</span></p><p>1.&nbsp;&nbsp;&nbsp; 分管学生教育管理工作，负责制订学生工作计划。</p><p>2.&nbsp;&nbsp;&nbsp; 负责和指导学生的思想政治教育工作。</p><p>3.&nbsp;&nbsp;&nbsp; 负责辅导员、班主任管理工作，指导辅导员、班主任开展工作。</p><p>4.&nbsp;&nbsp;&nbsp; 负责迎新和指导学生毕业生就业工作。</p><p>5.&nbsp;&nbsp;&nbsp; 负责组织和指导学生的军训、勤工俭学、奖助学金的评审工作。</p><p>6.&nbsp;&nbsp;&nbsp; 协助党委做好全系的党建工作、思想政治工作及宣传工作。</p><p>7.&nbsp;&nbsp;&nbsp; 负责本系教职工政治理论学习。</p><p>8.&nbsp;&nbsp;&nbsp; 完成学院、系交办的其他任务。</p><p>&nbsp;</p><p><span style=\"box-sizing: border-box; line-height: inherit; color: rgb(227, 61, 102);\">三、系办主任工作职责</span></p><p>1.&nbsp;&nbsp;&nbsp; 在系党总支、系行政领导的领导下，负责系日常行政工作。</p><p>2.&nbsp;&nbsp;&nbsp; 协助系主任组织本系部的专业建设、课程建设，协助系主任完成教学环节管理、教学运行管理，参与教学质量监控和督导工作，安排本系学生的实践教学环节。</p><p>3.&nbsp;&nbsp;&nbsp; 做好本系教学资料等档案的管理工作，起草系工作计划、工作总结。</p><p>4.&nbsp;&nbsp;&nbsp; 做好上级来文的分发，报送上级批文及文件的存档管理工作，做好系有关会议和重大活动的安排和记录工作，以及各类活动的接待工作。</p><p>5.&nbsp;&nbsp;&nbsp; 组织好本系教职工职称评审的有关工作，做好教师业务、职称档案建立。</p><p>6.&nbsp;&nbsp;&nbsp; 协助组织完成考勤、考核工作。</p><p>7.&nbsp;&nbsp;&nbsp; 做好系、教研室报刊征订工作。</p><p>8.&nbsp;&nbsp;&nbsp; 负责按时收集并上报本系教师授课信息，负责本系部机动监考教师调度；</p><p>9.&nbsp;&nbsp;&nbsp; 协调本系与各行政职能部门之间的关系，做好这些行政部门交办的有关行政工作。</p><p>10.&nbsp;&nbsp;&nbsp;完成系领导交办的其他工作。</p><p>&nbsp;</p><p><span style=\"box-sizing: border-box; line-height: inherit; color: rgb(227, 61, 102);\">四、教研室主任职责</span></p><p>1.&nbsp;&nbsp;&nbsp; 组织本专业教学计划和教学大纲的制定与修改。</p><p>2.&nbsp;&nbsp;&nbsp; 执行学院有关教学的规章制度，安排各学期的教学任务，稳定教学秩序，防止教学事故，保证教学质量，组织青年教师试讲，组织教学示范和观摩（每学期至少1次），组织教学研究活动（每学期至少8次）。</p><p>3.&nbsp;&nbsp;&nbsp; 研究、制定并落实学科发展计划，组织科研和学术梯队的建设，积极创造条件开展科研工作。搞好学术交流活动（每学期至少1次），形成良好的学术氛围。 教师培养工作，协助系主任进行本教研室的建设任务。</p><p>4.&nbsp;&nbsp;&nbsp; 研究本学科教材建设规划，安排年度教材、实习指导书编写,并组织实施。</p><p>5.&nbsp;&nbsp;&nbsp; 负责配合党支部作好教师思想工作，关心群众生活。</p><p>6.&nbsp;&nbsp;&nbsp; 定期总结工作，搞好文档建设与管理。</p><p>7.&nbsp;&nbsp;&nbsp; 协助系主任，履行教学常规教学职责。</p><p>8.&nbsp;&nbsp;&nbsp; 完成学院及系主任交办的其他任务。</p><p>&nbsp;</p><p><span style=\"box-sizing: border-box; line-height: inherit; color: rgb(227, 61, 102);\">五、教师职责</span></p><p>1.&nbsp;&nbsp;&nbsp; 为人师表，教书育人，严格按照教育规律办事，努力提高教学质量。</p><p>2.&nbsp;&nbsp;&nbsp; 严格按大纲要求认真备课并作好备课讲义，讲课应突出重点、讲好难点、板书工整、衣着整齐、发音准确，按时交开学初教学资料和期未教学资料。</p><p>3.&nbsp;&nbsp;&nbsp; 应作好辅导、答疑工作，安排好答疑时间和地点，一般以个别答疑为主，适当进行集中辅导</p><p>4.&nbsp;&nbsp;&nbsp; 在教学过程中应进行考勤和安排的作业，作业认真批改。并按规定比例计入学生平时成绩。</p><p>5.&nbsp;&nbsp;&nbsp; 应积极参与课程建设和课程改革、积极参加教学研究、不断总结和改进教学方法，尽可能采用电化教学、多媒体教学和网络教学等现代化教学手段，努力提高教学质量。</p><p>6.&nbsp;&nbsp;&nbsp; 指导毕业论文时选题应符合毕业论文的水平要求，培养学生理论联系实际、解决实际问题的能力，尤其要注意培养学生的创新思维和创新能力</p><p>7.&nbsp;&nbsp;&nbsp; 教师私自不行进行停课、调课、代课，必须提出申请主管教学的领导和教务处的批准，方可进行。</p><p>8.&nbsp;&nbsp;&nbsp; 积极参加科研工作和实验室建设工作，积极发表论文，积极参加学术会议和学术交流活动，不断提高科研和学术水平。跟踪本学科的理论和技术发展，并在课堂教学中反映出来</p><p>9.&nbsp;&nbsp;&nbsp; 服从教研室主任安排的教学和其他工作任务</p><p>&nbsp;</p><p><span style=\"box-sizing: border-box; line-height: inherit; color: rgb(227, 61, 102);\">六、学生辅导员职责</span></p><p>1.&nbsp;&nbsp;&nbsp; 要旗帜鲜明地对学生进行马列主义、毛泽东思想和邓小平理论教育，党的路线、方针、政策教育，坚持四项基本原则教育，爱国主义和共产主义道德教育。引导学生为实现四化、振兴中华而努力学习，成为四化建设的合格人才。</p><p>2.&nbsp;&nbsp;&nbsp; 负责组织实施系的学生工作计划。</p><p>3.&nbsp;&nbsp;&nbsp; 负责对学生进行形式政策教育，进行思想政治教育课。</p><p>4.&nbsp;&nbsp;&nbsp; 教育和督促学生，按时缴纳学费，履行交费义务，做好欠费学生的学费催缴工作,</p><p>5.&nbsp;&nbsp;&nbsp; 负责学生日常的思想政治教育工作，与班主任配合积极组织学生开展学术活动、社会实践及文体方面的活动，并配合有关部门做好学生管理方面的工作。</p><p>6.&nbsp;&nbsp;&nbsp; 要关心、了解学生，熟悉所管学生的个人情况、学习情况、平时表现、兴趣爱好等，有针对性地做好思想教育工作。</p><p>7.&nbsp;&nbsp;&nbsp; 经常深入到学生中去，了解情况，发现问题及时处理，对自己解决不了的问题要向上级有关部门反映。对突发事件和重大问题要及时妥善处理并向上级报告。</p><p>8.&nbsp;&nbsp;&nbsp; 实行弹性工作制，要利用学生课余时间开展工作。</p><p>9.&nbsp;&nbsp;&nbsp; 经常与班主任、任课教师和学生家长取得联系，沟通情况，共同做好学生工作。</p><p>10.&nbsp;&nbsp;负责调整班干部，指导团支部、班委会开展工作。</p><p>11.&nbsp;&nbsp;开展调查研究，针对一个时期学生的思想状况、典型人物、典型事件开展调研，写出调查报告。一般情况要求每位辅导员一学期至少写一份调查报告。</p><p>12.&nbsp;&nbsp;开展教育研究活动，不断探索教育工作的规律和做好学生思想政治教育工作的新方法、新途径。</p><p>13.&nbsp;&nbsp;参与对学生的奖惩工作。</p><p>14.&nbsp;&nbsp;协助系领导做好选拔优秀学生、迎新新生和毕业生就业组织工作。</p><p>15.&nbsp;&nbsp;协助班主任做好学生的总结鉴定和德智体综合测评工作。</p><p>16.&nbsp;&nbsp;做好奖学金的评定和学生困难补助、学生活动经费的申请发放工作。</p><p>17.&nbsp;&nbsp;配合系书记，负责学生档案材料的短期保管并按规定及时归档。</p><p>18.&nbsp;&nbsp;配合系书记，共同组织好学生的公益劳动和学生军训工作。</p><p>19.&nbsp;&nbsp;配合系书记，做好学生开学注册、放假离校的组织工作。</p><p><span style=\"color: rgb(51, 51, 51); font-family: &quot;Helvetica Neue&quot;, Helvetica, Roboto, Arial, sans-serif; font-size: 14px; text-indent: 28px; background-color: rgb(255, 255, 255);\">完成上级部门及系领导交办的其他任务。</span></p><p><br/></p>', '', '', '测试', '管理员', 0, 0, 1, 24, 29, 4, 1557998606, 1557998606),(47, '计算机系各专业简介', '测试', '<p><span style=\"box-sizing: border-box;color: rgb(51, 51, 51);font-family: &#39;Helvetica Neue&#39;, Helvetica, Roboto, Arial, sans-serif;font-size: 14px;text-indent: 28px;background-color: rgb(255, 255, 255)\"><p style=\"box-sizing: border-box;padding: 0px;font-family: inherit;font-size: 1rem;line-height: 20px;text-rendering: optimizelegibility;text-align: center;text-indent: 31px\"><span style=\"box-sizing: border-box\"><strong style=\"box-sizing: border-box;line-height: inherit\"><span style=\"font-size:18px\">软件技术专业</span></strong></span></p><p style=\"box-sizing: border-box;padding: 0px;font-family: inherit;font-size: 1rem;line-height: 20px;text-rendering: optimizelegibility;text-align: center;text-indent: 31px\"><span style=\"box-sizing: border-box;line-height: 20px;font-family: Verdana, sans-serif;font-size: 16px\"><strong style=\"box-sizing: border-box;line-height: inherit\"><span style=\"font-size:18px\">&nbsp;</span></strong></span></p></span></p><p style=\"box-sizing: border-box;padding: 0px;font-family: &#39;Helvetica Neue&#39;, Helvetica, Roboto, Arial, sans-serif;font-size: 1rem;line-height: 27px;text-rendering: optimizelegibility;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255);text-indent: 28px\"><br/></p><p style=\"box-sizing: border-box;margin-top: 0px;margin-bottom: 1.25rem;padding: 0px;font-family: &#39;Helvetica Neue&#39;, Helvetica, Roboto, Arial, sans-serif;font-size: 1rem;line-height: 27px;text-rendering: optimizelegibility;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255);text-indent: 28px\"><span style=\"box-sizing: border-box;line-height: inherit;color: rgb(227, 61, 102)\"><span style=\"box-sizing: border-box;color: rgb(51, 51, 51);font-family: 宋体\">培养目标：</span></span><span style=\"box-sizing: border-box;font-family: 宋体\">本专业培养具有扎实的基础理论知识和熟练的操作技能，能胜任网站开发与维护、软件开发与测试、移动应用产品开发与运营、数据库开发与管理、计算机文档处理等工作，具有良好的职业素养、创新能力的高素质技能型人才。</span></p><p style=\"box-sizing: border-box;margin-top: 0px;margin-bottom: 1.25rem;padding: 0px;font-family: &#39;Helvetica Neue&#39;, Helvetica, Roboto, Arial, sans-serif;font-size: 1rem;line-height: 27px;text-rendering: optimizelegibility;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255);text-indent: 28px\"><span style=\"box-sizing: border-box;line-height: inherit;color: rgb(227, 61, 102)\"><span style=\"box-sizing: border-box;font-family: 宋体\">课程体系：</span></span><span style=\"box-sizing: border-box;font-family: 宋体\">数据库应用与开发、</span><span style=\"box-sizing: border-box;font-family: 宋体\">PHP</span><span style=\"box-sizing: border-box;font-family: 宋体\">网站开发、</span><span style=\"box-sizing: border-box;font-family: 宋体\">Java</span><span style=\"box-sizing: border-box;font-family: 宋体\">程序设计、</span><span style=\"box-sizing: border-box;font-family: 宋体\">JSP&nbsp;</span><span style=\"box-sizing: border-box;font-family: 宋体\">程序设计、</span><span style=\"box-sizing: border-box;font-family: 宋体\">.net WEB</span><span style=\"box-sizing: border-box;font-family: 宋体\">开发技术、<span style=\"box-sizing: border-box\">C#</span>程序设计、管理信息系统开发与应用、手机应用开发、</span><span style=\"box-sizing: border-box;font-family: 宋体\">微信公众平台开发</span><span style=\"box-sizing: border-box;font-family: 宋体\">、服务器操作系统、网页设计与制作、图形图像处理等</span><span style=\"box-sizing: border-box;font-family: 宋体\">。</span></p><p style=\"box-sizing: border-box;margin-top: 0px;margin-bottom: 1.25rem;padding: 0px;font-family: &#39;Helvetica Neue&#39;, Helvetica, Roboto, Arial, sans-serif;font-size: 1rem;line-height: 27px;text-rendering: optimizelegibility;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255);text-indent: 28px\"><span style=\"box-sizing: border-box;line-height: inherit;color: rgb(227, 61, 102)\"><span style=\"box-sizing: border-box;font-family: 宋体\">技能考证：</span></span><span style=\"box-sizing: border-box;font-family: 宋体\">网页制作、</span><span style=\"box-sizing: border-box\">VB</span><span style=\"box-sizing: border-box;font-family: 宋体\">等</span><span style=\"box-sizing: border-box;font-family: 宋体\">。</span></p><p style=\"box-sizing: border-box;margin-top: 0px;margin-bottom: 1.25rem;padding: 0px;font-family: &#39;Helvetica Neue&#39;, Helvetica, Roboto, Arial, sans-serif;font-size: 1rem;line-height: 27px;text-rendering: optimizelegibility;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255);text-indent: 28px\"><span style=\"box-sizing: border-box;line-height: inherit;color: rgb(227, 61, 102)\"><span style=\"box-sizing: border-box;font-family: 宋体\">就业面向</span></span><span style=\"box-sizing: border-box;font-family: 宋体\">：</span><span style=\"box-sizing: border-box;font-family: 宋体\">广告、印刷与装饰设计公司、媒体设计部门、</span><span style=\"box-sizing: border-box;font-family: 宋体\">政府机关</span><span style=\"box-sizing: border-box;font-family: 宋体\">高素质技能型人才</span><span style=\"box-sizing: border-box;font-family: 宋体\">。</span></p><p style=\"box-sizing: border-box;margin-top: 0px;margin-bottom: 1.25rem;padding: 0px;font-family: &#39;Helvetica Neue&#39;, Helvetica, Roboto, Arial, sans-serif;font-size: 1rem;line-height: 27px;text-rendering: optimizelegibility;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255);text-indent: 28px\"><span style=\"box-sizing: border-box;line-height: inherit;color: rgb(227, 61, 102)\"><span style=\"box-sizing: border-box;font-family: 宋体\">课程体系：</span></span><span style=\"box-sizing: border-box;font-family: 宋体\">计算机组装与维护；网站建设与维护；局域网技术与组网工程；网络操作系统；网络互连与路由技术；网页设计与制作；综合布线实施与管理；网络安全管理与维护；网络设备应用；系统集成项目管理；<span style=\"box-sizing: border-box\">Linux</span>应用技术；<span style=\"box-sizing: border-box\">PHP</span>动态网页设计等。</span></p><p style=\"box-sizing: border-box;margin-top: 0px;margin-bottom: 1.25rem;padding: 0px;font-family: &#39;Helvetica Neue&#39;, Helvetica, Roboto, Arial, sans-serif;font-size: 1rem;line-height: 27px;text-rendering: optimizelegibility;color: rgb(51, 51, 51);white-space: normal;background-color: rgb(255, 255, 255);text-indent: 28px\"><span style=\"box-sizing: border-box;line-height: inherit;color: rgb(227, 61, 102)\"><span style=\"box-sizing: border-box;font-family: 宋体\">技能考证：</span></span><span style=\"box-sizing: border-box;font-family: 宋体\">全国计算机等级考试三级（网络）认证考试；全国计算机技术与软件专业技术资格（水平）考试网络工程师认证考试；</span><span style=\"box-sizing: border-box;font-family: 宋体\">与<span style=\"box-sizing: border-box\">&nbsp;Cisco</span>培训企业合作，提供<span style=\"box-sizing: border-box\">Cisco</span>体系认证考试（<span style=\"box-sizing: border-box\">CCNA</span>）。</span></p><p><span style=\"color: rgb(51, 51, 51);font-family: &#39;Helvetica Neue&#39;, Helvetica, Roboto, Arial, sans-serif;font-size: 14px;text-indent: 28px;background-color: rgb(255, 255, 255)\">&nbsp;&nbsp;&nbsp;</span><span style=\"font-size:18px\"><span style=\"box-sizing: border-box;line-height: inherit;color: rgb(227, 61, 102)\"><span style=\"box-sizing: border-box;font-family: 宋体\">就业面向：</span></span><span style=\"box-sizing: border-box;font-family: 宋体\">面向政府机关、企事业单位、<span style=\"box-sizing: border-box\">IT</span>企业、各类外资企业、教育、科研等各个行业从事计算机网络系统的规划、组建、管理与维护，网站建设与维护，网络系统集成的安装与测试工作、各种网络服务站点的业务工作以及各种网络服务站点的信息收集与组织、网络设备销售与技术支持等工作</span><span style=\"box-sizing: border-box;font-family: 宋体\">以及自主创业</span></span><span style=\"font-size:18px\"><span style=\"box-sizing: border-box;font-family: 宋体;font-size: 14px\">等。</span></span><span style=\"box-sizing: